create database task_1;
use task_1;
create table windows(width float(10),height float(10), material varchar(10), Style varchar(10), brand_name varchar(20), Frame_color varchar(10), Frame_finish varchar(10), window_security varchar(10), locking varchar(10), weather_resistance varchar(10));
create table washing_machine( Capacity int, Load_Type varchar(20), temp_setting varchar(20), prog_setting varchar(10), allergen_removal varchar (10), drum_material varchar(10), drum_cleaning varchar(20), brand varchar(10), prize float, drum_shape varchar (10), warranty varchar(10));
create table broom_stick(Material varchar(10), length varchar(10), handle_grip varchar(10),Weight varchar(10), Bristle_Color varchar(10), Flexibility varchar(10),brand varchar(10), prize float, Versatility varchar(10),Handle_Material varchar(10));
create table coffee_estate(location varchar(20), Climate varchar(10), Soil_type varchar(10), Coffee_varieties varchar(10), processing_method varchar(20), visitor_facilities varchar(10), total_area float, Planting_Density varchar(10),Harvesting_Method varchar(20), No_of_workers int);
create table wardrobe( material varchar(10), size varchar(10), style varchar(10), finish varchar(10), color varchar(10), capacity varchar(10), hanging_hooks varchar(10), clothing_protection varchar(10),no_of_doors int, no_of_drawers int);
create table pizza(size varchar(10), Delicious varchar(10), Ratings int, Portable varchar(10), quality varchar(10), quantity varchar(10), Nutritious varchar(10), Shareable varchar(10), quicks varchar(10), Ionic varchar(10));
create table Movie(production varchar(10), voice_dub varchar(10), Hero varchar(10), visuals varchar(10),characters varchar(20), heroin varchar(10), lightboys varchar(10), music varchar(10), director varchar(10),cameraman varchar(10) );
create table blood( no_of_A_postive_blood int, no_of_A_negative_blood int, no_of_B_postive_blood int, no_of_B_negative_blood int, no_of_AB_postive_blood int, no_of_AB_negative_blood int, no_of_O_postive_blood int, no_of_O_negative_blood int, total_no_of_donors int, donors_list varchar(10));
create table cardologist(patient_name varchar(10), patirnt_record varchar(10), appointment varchar(10), tests varchar(10), medication varchar(10), doctor_fees float, procedures varchar(10),BP_Readings varchar(10), side_effects varchar(10), referal varchar(10));
create table oven(brand varchar(10), model varchar(10), prizce float, capacity varchar(10), colour varchar(10), power_source varchar(10), energy_rating int, warranty_period float,repair_description varchar(10),temp_used varchar(10));